import React, { useEffect, useState } from 'react';
import './App.css';

interface RegionData {
  data: any; // Adjust based on the actual data structure
  lastChecked: string;
}

interface EndpointData {
  [region: string]: RegionData;
}

const App: React.FC = () => {
  const [data, setData] = useState<EndpointData | null>(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080');

    ws.onmessage = (event) => {
      const receivedData: EndpointData = JSON.parse(event.data);
      setData(receivedData);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onerror = () => {
      console.log('WebSocket error');
    };

    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>DevOps Monitoring Dashboard</h1>
        {data ? (
          <div className="status-board">
            {Object.entries(data).map(([region, details]) => (
              <div key={region} className="region-status">
                <h2>{region.toUpperCase()}</h2>
                <p className="status">Status: {details.data.status}</p>
                <p className="lastChecked">Last Checked: {new Date(details.lastChecked).toLocaleString()}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>Loading data...</p>
        )}
      </header>
    </div>
  );
};

export default App;
